import React from "react";
import {
  ListGroup,
  ListGroupItem,
  Row,
  Col,
  Form,
  FormInput,
  FormGroup,
  Button
} from "shards-react";
import { CREATEPROVINSI } from '../../utils/url'
import axios from 'axios';

const AddProvinsiForm = () => {
  const [nama,setNama]=useState("");
  const [password,setPassword]=useState("");
  //const history = useHistory();

  const onSubmit = () => {
    axios
        .post(LOGIN, {
            nama: nama,
            password: password
        })
        .then((res) => {
            setNama(res.nama);
            setPassword(res.password);
            login(res.data.data.id);
            //history.push("/daerah")
        }).catch((err) => {
            alert("Username or Password Wrong" + " (" + err.message + ")");
            console.log(err);
        });
  }
  return(
    <ListGroup flush>
      <ListGroupItem className="p-3">
        <Row>
          <Col>
            <Form>
              <FormGroup>
                <label htmlFor="namaHakAkses">Nama Provinsi</label>
                <FormInput id="namaHakAkses" placeholder="Jawa Timur" />
              </FormGroup>
              <Button type="submit" className="d-flex ml-auto mr-auto ml-sm-auto mr-sm-3 mt-3 mt-sm-0">Create Provinsi</Button>
            </Form>
          </Col>
        </Row>
      </ListGroupItem>
    </ListGroup>
  );
  
}

export default AddProvinsiForm;
