import React, { useEffect, useState } from "react";
import {
  ListGroup,
  ListGroupItem,
  Row,
  Col,
  Form,
  FormInput,
  FormGroup,
  FormCheckbox,
  FormSelect,
  Button
} from "shards-react";

const LoginForm = () => {
  const [email,setNamaAdmin]=useState("");
  const [password,setPassword]=useState("");
  const history = React.useHistory();

  const useEffect = () => {
    if(localStorage.getItem('user')){
      history.pushState("/add")
    }
  }
  return(
    <ListGroup flush>
      <ListGroupItem className="p-3">
        <Row>
          <Col>
            <Form>
              <FormGroup>
                <label htmlFor="namaAdmin">Nama Admin</label>
                <FormInput id="namaAdmin" placeholder="Nama Admin" 
                onChange={(e) => setNamaAdmin(e.target.value)}/>
              </FormGroup>
              <FormGroup>
                <label htmlFor="password">Password</label>
                <FormInput id="password" placeholder="Password" 
                onChange={(e) => setPassword(e.target.value)}/>
              </FormGroup>
              <Button type="submit" className="d-flex ml-auto mr-auto ml-sm-auto mr-sm-auto mt-3 mt-sm-4">Login as Admin</Button>
            </Form>
          </Col>
        </Row>
      </ListGroupItem>
    </ListGroup>
  )
  }

export default LoginForm;
