import React, { useEffect, useState } from "react"
import {
  ListGroup,
  ListGroupItem,
  Row,
  Col,
  Form,
  FormInput,
  FormGroup,
  Button
} from "shards-react"

import { useHistory } from "react-router-dom"



const LoginForm = () =>{
  const [nama,setNama]=useState("");
  const [password,setPassword]=useState("");
  const history = useHistory();

  useEffect(() => {
    if(localStorage.getItem('user')){
      history.pushState("/")
    }
  },[])

  async function login(){
    console.warn(nama,password)
    let item=(nama,password);
    let result= await fetch("http://localhost:8000/api/login",{
      method: "POST",
      body: JSON.stringify(item)
    });
    result = await result.json();
    localStorage.setItem("user", JSON.stringify(result))
    history.push("/hak-akses")
  }
  return(
    <ListGroup flush>
      <ListGroupItem className="p-3">
        <Row>
          <Col>
            <Form>
              <FormGroup>
                <label htmlFor="namaAdmin">Nama Admin</label>
                <FormInput id="namaAdmin" placeholder="Nama Admin" 
                onChange={(e)=>setNama(e.target.value)} />
              </FormGroup>
              <FormGroup>
                <label htmlFor="password">Password</label>
                <FormInput type="password" id="password" placeholder="Password"
                onChange={(e)=>setPassword(e.target.value)}/>
              </FormGroup>
              <Button onClick={login} type="submit" className="d-flex ml-auto mr-auto ml-sm-auto mr-sm-auto mt-3 mt-sm-4">Login as Admin</Button>
            </Form>
          </Col>
        </Row>
      </ListGroupItem>
    </ListGroup>
  )
  
}

export default LoginForm
